 import React, {Component, Fragment} from 'react';
 import {connect} from 'react-redux';
 import {CSSTransition} from 'react-transition-group';
 import { createGlobalStyle } from 'styled-components';
 import {
   HeaderWrapper,
   Logo,
   Nav,
   NavItem,
   NavSearch,
   Addition,
   Button,
   SearchWrapper
 } from './style';
 import '../../statics/Iconfont/iconfont.js';
import { GlobalStyle } from '../../statics/Iconfont/iconfont.js';

const Header = (props) => {
  <Fragment>
  <GlobalStyle/>
  <HeaderWrapper>
    <Logo/>

    <Nav>
      <NavItem className='left active'>首页</NavItem>
      <NavItem className='left'>下载App</NavItem>
      <NavItem className='right'>登录</NavItem>
      <NavItem className='right'>
        <span className="iconfont">&#xe636;</span>
      </NavItem>
      <SearchWrapper>
        <CSSTransition
          in={this.props.focused}
          timeout={200}
          classNames="slide"
        >
        <NavSearch
          className = {props.focused ? 'focused': ''}
          onFocus = {props.handleInputFocus}
          onBlur = {props.handleInputBlur}
        ></NavSearch>
      </CSSTransition>
      <span className = {props.focused ? 'focused iconfont': 'iconfont'}>&#xe729;</span> 
      </SearchWrapper>
      <Addition>
        <Button className='writting'><span className="iconfont">&#xe6e5;</span>
写文章</Button>
        <Button className='reg'>注册</Button>
      </Addition>
    </Nav>
  </HeaderWrapper>
 </Fragment>
}



const mapStateToProps = (state) => {
  return {
    focused: state.focused
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    handleInputFocus() {
      const action = {
        type: 'search_focus'
      };
      dispatch(action);
    }, 
    handleInputBlur() {
      const action = {
        type: 'search_blur'
      };
      dispatch(action);
    }
  }
}

 export default connect(mapStateToProps, mapDispatchToProps)(Header);
