 import React, {Component, Fragment} from 'react';
 import {connect} from 'react-redux';
 import {CSSTransition} from 'react-transition-group';
 import {actionCreators} from './store';
 import {
   HeaderWrapper,
   Logo,
   Nav,
   NavItem,
   NavSearch,
   Addition,
   Button,
   SearchWrapper,
   SearchInfo,
   SearchInfoTitle,
   SearchInfoSwitch,
   SearchInfoItem,
   SearchInfoList
 } from './style';
 import '../../statics/Iconfont/iconfont.js';
import { GlobalStyle } from '../../statics/Iconfont/iconfont.js';

class Header extends Component {

  getListArea()  {
    if(this.props.focused) {
      return (<SearchInfo>
        <SearchInfoTitle>
          热门搜索
          <SearchInfoSwitch>换一批</SearchInfoSwitch>
        </SearchInfoTitle>
        <SearchInfoList>
        {
          this.props.list.map((item)=>{
          return <SearchInfoItem key={item}>{item}</SearchInfoItem>
          })
        }
        </SearchInfoList>
      </SearchInfo>);
    } else {
      return null;
    }
  };

  render() {
    return(
      <Fragment>
      <GlobalStyle/>
      <HeaderWrapper>
        <Logo/>
  
        <Nav>
          <NavItem className='left active'>首页</NavItem>
          <NavItem className='left'>下载App</NavItem>
          <NavItem className='right'>登录</NavItem>
          <NavItem className='right'>
            <span className="iconfont">&#xe636;</span>
          </NavItem>
          <SearchWrapper>
            <CSSTransition
              in={this.props.focused}
              timeout={200}
              classNames="slide"
            >
            <NavSearch
              className = {this.props.focused ? 'focused': ''}
              onFocus = {this.props.handleInputFocus}
              onBlur = {this.props.handleInputBlur}
            ></NavSearch>
          </CSSTransition>
          <span className = {this.props.focused ? 'focused iconfont': 'iconfont'}>&#xe729;</span> 
          
          {this.getListArea()}
          </SearchWrapper>
          <Addition>
            <Button className='writting'><span className="iconfont">&#xe6e5;</span>
    写文章</Button>
            <Button className='reg'>注册</Button>
          </Addition>
        </Nav>
      </HeaderWrapper>
    </Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    focused: state.getIn(['header', 'focused']),
     //state.get('header').get('focused')
    list: state.getIn(['header', 'list'])
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    handleInputFocus() {
      dispatch(actionCreators.getList());
      dispatch(actionCreators.searchFocus());
    }, 
    handleInputBlur() {
      dispatch(actionCreators.searchBlur());
    }
  }
}

 export default connect(mapStateToProps, mapDispatchToProps)(Header);
