 import React, {Component, Fragment} from 'react';
 import {CSSTransition} from 'react-transition-group';
 import { createGlobalStyle } from 'styled-components';
 import {
   HeaderWrapper,
   Logo,
   Nav,
   NavItem,
   NavSearch,
   Addition,
   Button,
   SearchWrapper
 } from './style';
 import '../../statics/Iconfont/iconfont.js';
import { GlobalStyle } from '../../statics/Iconfont/iconfont.js';

 class Header extends Component {

  constructor(props) {
    super(props);
    this.state = {
      focused: false
    }
    this.handleInputFocus = this.handleInputFocus.bind(this);
    this.handleInputBlur  = this.handleInputBlur.bind(this);
  }

   render() {
     return (
       <Fragment>
        <GlobalStyle/>
        <HeaderWrapper>
          <Logo/>

          <Nav>
            <NavItem className='left active'>首页</NavItem>
            <NavItem className='left'>下载App</NavItem>
            <NavItem className='right'>登录</NavItem>
            <NavItem className='right'>
              <span className="iconfont">&#xe636;</span>
            </NavItem>
            <SearchWrapper>
              <CSSTransition
                in={this.state.focused}
                timeout={200}
                classNames="slide"
              >
              <NavSearch
                className = {this.state.focused ? 'focused': ''}
                onFocus = {this.handleInputFocus}
                onBlur = {this.handleInputBlur}
              ></NavSearch>
            </CSSTransition>
            <span className = {this.state.focused ? 'focused iconfont': 'iconfont'}>&#xe729;</span> 
            </SearchWrapper>
            <Addition>
              <Button className='writting'><span className="iconfont">&#xe6e5;</span>
写文章</Button>
              <Button className='reg'>注册</Button>
            </Addition>
          </Nav>
        </HeaderWrapper>
       </Fragment>
     )
   }

   handleInputFocus() {
     this.setState({
       focused: true
     })
   }

   handleInputBlur() {
     this.setState({
       focused: false
     })
   }

 }

 export default Header;
